    private void delete_ButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_delete_ButtonActionPerformed
        String accounting_id = Accounting_id_TextField.getText();
        String income = income_TextField.getText();
        String electronic_bil = electronic_bill_TextField.getText();
        String water_bill = water_bill_TextField.getText();
        String emp_pay = emp_pay_TextField.getText();
        String main_tain = main_tain_TextField.getText();
        String benefit = benefit_TextField.getText();
        String apartment_id = apartment_id_TextField.getText();
        try {
            if (my.deleteAccountingData(accounting_id)) {
                DefaultTableModel model = (DefaultTableModel) Accounting_Table.getModel();
                model.setRowCount(0);
                ResultSet rs = my.getData("Accounting");
                while (rs.next()) {
                    String aaccounting_id = rs.getString("acc_id");
                    String iincome = rs.getString("income");
                    String eelectronic_bill = rs.getString("electronic_bill");
                    String wwater_bill = rs.getString("water_bill");
                    String eemp_pay = rs.getString("emp_pay");
                    String mmain_tain = rs.getString("main_tain");
                    String bbenefit = rs.getString("benefit");
                    String aapartment_id = rs.getString("apart_id");
                    model.addRow(new String[]{aaccounting_id, iincome, eelectronic_bill, wwater_bill, eemp_pay, mmain_tain, bbenefit, aapartment_id});
                }
            }JOptionPane.showMessageDialog(null,"DELETE" );
        } catch (Exception exc) {
            JOptionPane.showMessageDialog(null,"Err: " + exc);
        }
    }//GEN-LAST:event_delete_ButtonActionPerformed

